// Bar

var bar = new ProgressBar.Line(htmlcss, {
    strokeWidth: 4,
    easing: 'easeInOut',
    duration: 5000,
    color: '#01D5DA',
    trailColor: '#eee',
    trailWidth: 4,
    svgStyle: {
        width: '100%',
        height: '100%'
    },
    from: {
        color: '#01D5DA'
    },
    to: {
        color: '#01D5DA'
    },
    step: (state, bar) => {
        bar.path.setAttribute('stroke', state.color);
    }
});

